const crypto = global.get('crypto');
const fs = global.get('fs');
// keep a default topic in flow if user picked one manually
const topic_name = flow.get("topic_name") || '';
const wifi_ssid = String(msg.payload || '').trim();
flow.set('wifi_ssid', wifi_ssid);
let deviceID; let attempts = 0;
while (!deviceID && attempts < 5) {
  deviceID = global.get('deviceID','file');
  if (!deviceID) { attempts++; const until = Date.now()+200; while(Date.now()<until){} }
}
if (!deviceID) { node.error('‚ùå DeviceID not found'); return null; }
// derive shared secret once (used to AES-encrypt the register payload)
const certDir = "/etc/mosquitto/certs";
const alicePriv = `${certDir}/alice-private-key.pem`;
const sunnyPub  = `${certDir}/sunny-public-key.pem`;
const outSecret = `${certDir}/alice-topic-registration-secret-key.bin`;
msg.payload = `openssl pkeyutl -derive -inkey ${alicePriv} -peerkey ${sunnyPub} -out ${outSecret}`;
return msg;

const fs = global.get('fs');
const crypto = global.get('crypto');

// Pull ctx from msg or flow (Plan A robustness)
const ctx = msg.kms_ctx || flow.get('current_run_ctx') || {};

// topic from ctx or flow
const topic_name = String(ctx.topic_name || flow.get('topic_name') || '').trim();
if (!topic_name) { node.error('Missing topic_name'); return null; }

const wifi_ssid = String(flow.get('wifi_ssid') || '').trim();
if (!wifi_ssid) { node.error('Missing wifi_ssid'); return null; }

const deviceID = global.get('deviceID','file');
if (!deviceID) { node.error('Missing deviceID'); return null; }

// Derive AES key from ECDH secret
const secretPath = '/etc/mosquitto/certs/alice-topic-registration-secret-key.bin';
if (!fs.existsSync(secretPath)) { node.error(`Missing ${secretPath}`); return null; }
const secretBin = fs.readFileSync(secretPath);
const aesKey = crypto.createHash('sha256').update(secretBin).digest();

// Sign `${deviceID}|${topic_name}` using Alice private key
const privateKeyPEM = global.get('private_key','file');
if (!privateKeyPEM) { node.error('Alice private key missing in global(file)'); return null; }
const signer = crypto.createSign('sha256');
signer.update(`${deviceID}|${topic_name}`);
signer.end();
const signature = signer.sign(privateKeyPEM, 'base64');

// Encrypt registration payload
const iv = crypto.randomBytes(16);
const cipher = crypto.createCipheriv('aes-256-cbc', aesKey, iv);
const body = JSON.stringify({ deviceID, topic_name });
let encrypted = cipher.update(body,'utf8','hex');
encrypted += cipher.final('hex');

// Build message (with meta + seq/total)
msg.payload = {
  encryptedData: encrypted,
  iv: iv.toString('hex'),
  signature,
  device_id: deviceID,
  wifi_ssid,
  type: 'data',
  t_pub: ctx.t_pub,
  run_id: ctx.run_id,
  run_tag: ctx.run_tag,
  mode: ctx.mode,
  path_class: ctx.path_class,
  mqtt_version: ctx.mqtt_version,
  qos: ctx.qos,
  rate_mps: ctx.rate_mps,
  msg_count: ctx.msg_count,
  duration: ctx.duration,
  seq: Number(ctx.seq || 0),
  total: Number(ctx.total || 0)
};

if (msg.kms_control) { msg.payload.kms_control = msg.kms_control; }

const runKey = String(ctx.run_id || ctx.run_tag || 'default');
const qKey = `kms_q_${runKey}`;
let q = flow.get(qKey) || [];
if (Number.isFinite(ctx.t_pub)) { q.push(ctx.t_pub); flow.set(qKey, q); }

return msg;

const mode   = String(msg.mode || 'secure');
const baseRate = Number(msg.rate_mps || 0);
const runId  = String(msg.run_id || Date.now());
const runTag = String(msg.run_tag || 'run');
const qosArg  = String(msg.qos ?? '');
const mqttArg = String(msg.mqtt_version ?? '');

const msgCountIn   = Number(msg.msg_count || 0);
const durationIn   = Number(msg.duration || 0);

let rate = baseRate;
let testSeconds = 120;
let total;

// --------- Choose semantics: KMS (rate-based) vs E2E (fixed #msgs) ----------
if (mode === 'e2e' && msgCountIn > 0) {
  // E2E: total messages = msg_count, duration = durationIn or msgCountIn
  total = msgCountIn;
  if (durationIn > 0) {
    testSeconds = durationIn;
  } else {
    // default: 1 msg per second
    testSeconds = msgCountIn;
  }
  if (!Number.isFinite(rate) || rate <= 0) {
    rate = msgCountIn / testSeconds;
  }
} else {
  // KMS: fixed 120-second window, total = rate * 120
  testSeconds = 120;
  total = Math.max(0, Math.floor(baseRate * testSeconds));
  rate = baseRate;
}

const every = rate > 0 ? Math.max(1, Math.floor(1000 / rate)) : 1000;

// --------- Store run context (also sent inside msgs to Sunny/Bob) ----------
const runCtx = {
  run_id: runId,
  run_tag: runTag,
  mode: msg.mode,
  path_class: msg.path_class,
  mqtt_version: msg.mqtt_version,
  qos: msg.qos,
  rate_mps: rate,
  msg_count: total,
  duration: testSeconds          // <-- this is the duration we‚Äôll pass to scripts
};
flow.set('current_run_ctx', runCtx);

// --------- Topic list / rotation ----------
const list = (flow.get('registeredTopics') || []).map(s => String(s)).filter(Boolean);
if (!list.length && flow.get('topic_name')) list.push(flow.get('topic_name'));
if (!list.length) { node.error('No topics in cache. Load topics.csv or pick one.'); return; }

const idxKey = `kms_topic_idx_${runId}`; flow.set(idxKey, 0);
const seqKey = `kms_seq_${runId}`;       flow.set(seqKey, 0);

// --------- Start monitor scripts on Sunny (now with duration arg) ----------
const durationArg = testSeconds.toString();   // 120s for KMS, N seconds for E2E

const cmd1 = `/etc/mosquitto/perf_results/monitor_sys_stats.sh ${runTag} mosquitto ${qosArg} ${mqttArg} ${durationArg}`;
const cmd2 = `/etc/mosquitto/perf_results/monitor_sys_stats.sh ${runTag} node-red ${qosArg} ${mqttArg} ${durationArg}`;
node.send([ null, { payload: cmd1 }, { payload: cmd2 }, null ]);

// --------- Generate per-message tick ----------
if (total === 0) {
  node.status({ fill:'yellow', shape:'ring', text:'total=0 (no sends)' });
  return;
}

const tick = setInterval(() => {
  let seq = Number(flow.get(seqKey) || 0) + 1;
  flow.set(seqKey, seq);

  let i = Number(flow.get(idxKey) || 0);
  const topic_name = list[i % list.length];
  flow.set(idxKey, (i + 1) % list.length);

  const ctx = { ...runCtx, topic_name, t_pub: Date.now(), seq, total };

  // Final packet carries kms_control: 'done'
  if (seq >= total) {
    clearInterval(tick);
    node.send([{ kms_ctx: ctx, kms_control: 'done' }, null, null, null]);
    node.status({ fill:'green', shape:'dot', text:`done ${seq}/${total} (${mode})` });
    return;
  }

  // Regular packet
  node.send([{ kms_ctx: ctx }, null, null, null]);

  // Status pulse
  const step = Math.max(1, Math.floor(rate));
  if (seq % step === 0) {
    node.status({
      fill:'blue',
      shape:'dot',
      text:`${mode}: ${seq}/${total} ‚Ä¢ ${rate.toFixed(2)} msg/s ‚Ä¢ topics:${list.length}`
    });
  }
}, every);

node.status({
  fill:'blue',
  shape:'dot',
  text:`start ${mode} ‚Ä¢ total=${total} ‚Ä¢ rate‚âà${rate.toFixed(2)} msg/s`
});
return;

// Decrypt topic-registration RESPOND, verify, store key, and forward run-meta (incl. msg_count, duration, t_pub)

const fs = global.get('fs');
const crypto = global.get('crypto');

// 0) Expect envelope { encryptedData(hex), iv(hex), signature(base64) }
const p = msg.payload || {};
const encHex = String(p.encryptedData || '').trim();
const ivHex  = String(p.iv || '').trim();
const sigB64 = String(p.signature || '').trim();
if (!encHex || !ivHex || !sigB64) {
  node.warn('‚ÑπÔ∏è Respond envelope missing enc/iv/sig ‚Äî ignoring.');
  return null;
}

// 1) Derive AES key from Alice‚Äôs topic-registration secret
const secretPath = '/etc/mosquitto/certs/alice-topic-registration-secret-key.bin';
let secretBin;
try {
  secretBin = fs.readFileSync(secretPath);
} catch (e) {
  node.error(`‚ùå Cannot read secret key at ${secretPath}: ${e.message}`);
  return null;
}
const aesKey = crypto.createHash('sha256').update(secretBin).digest();

// 2) Decrypt (AES-256-CBC)
let obj;
try {
  const iv = Buffer.from(ivHex, 'hex');
  const ct = Buffer.from(encHex, 'hex');
  if (iv.length !== 16) {
    node.warn(`‚ÑπÔ∏è Invalid IV length ${iv.length}; ignoring.`);
    return null;
  }
  const decipher = crypto.createDecipheriv('aes-256-cbc', aesKey, iv);
  const dec = Buffer.concat([decipher.update(ct), decipher.final()]).toString('utf8');
  obj = JSON.parse(dec);
} catch (e) {
  node.warn('‚ÑπÔ∏è Decryption/parse failed ‚Äî ignoring this message: ' + e.message);
  return null;
}

// 3) Verify signature with Sunny‚Äôs public key
let sunnyPub;
try {
  sunnyPub = fs.readFileSync('/etc/mosquitto/certs/sunny-public-key.pem', 'utf8').trim();
} catch (e) {
  node.error('‚ùå Cannot read sunny-public-key.pem: ' + e.message);
  return null;
}

const status    = String(obj.status || '');
const topicName = String(obj.topic_name || '');
const keyBlobB64Raw = String(obj.key_blob_b64 || '')
  .replace(/^"(.*)"$/, '$1')
  .replace(/\s+/g, '');
if (!status || !topicName || !keyBlobB64Raw) {
  node.warn('‚ÑπÔ∏è Decrypted payload missing status/topic_name/key_blob_b64 ‚Äî ignoring.');
  return null;
}

let verified = false;
try {
  const canonical = `${status}|${topicName}|${keyBlobB64Raw}`;
  const verifier = crypto.createVerify('sha256');
  verifier.update(canonical);
  verifier.end();
  verified = verifier.verify(sunnyPub, sigB64, 'base64');
} catch (e) {
  node.warn('‚ÑπÔ∏è Signature verify error ‚Äî ignoring: ' + e.message);
  return null;
}
if (!verified) {
  node.warn('‚ÑπÔ∏è Signature invalid ‚Äî ignoring.');
  return null;
}

// 4) Validate the key is really 32 bytes
let keyBuf;
try {
  const b64 = keyBlobB64Raw
    .replace(/-/g, '+')
    .replace(/_/g, '/')
    .padEnd(Math.ceil(keyBlobB64Raw.length / 4) * 4, '=');
  keyBuf = Buffer.from(b64, 'base64');
} catch (e) {
  keyBuf = Buffer.alloc(0);
}
if (keyBuf.length !== 32) {
  node.warn(`‚ÑπÔ∏è Invalid topic key length (got ${keyBuf.length} bytes) ‚Äî ignoring.`);
  return null;
}

//node.warn(`üîì Decrypt/Verify OK for topic "${topicName}" ‚Äî 32B key accepted.`);

// 5) Store key
const topicKeys = Object.assign({}, flow.get('topicKeys') || {});
topicKeys[topicName] = keyBlobB64Raw;  // keep as b64 for downstream
flow.set('topicKeys', topicKeys);

// 6) Forward KMS run/test metadata from Sunny (incl. msg_count, duration, t_pub)
const meta = {
  run_id:     String(obj.run_id || ''),
  run_tag:    String(obj.run_tag || ''),
  mode:       obj.mode,
  path_class: obj.path_class,
  mqtt_version: obj.mqtt_version,
  qos:        obj.qos,
  rate_mps:   obj.rate_mps,
  msg_count:  Number(obj.msg_count || 0),
  duration:   Number(obj.duration || 0),
  t_pub:      Number(obj.t_pub || 0)
};
flow.set('kms_meta_from_resp', meta);
msg.meta = meta;

msg.topicName  = topicName;
msg.keyBlobB64 = keyBlobB64Raw;
msg.payload = {
  status,
  topic_name: topicName,
  key_blob_b64: keyBlobB64Raw,
  verified: true
};

// simple counter for visual feedback
const cntKey = 'alice_resp_count';
let cnt = Number(flow.get(cntKey) || 0) + 1;
flow.set(cntKey, cnt);
node.status({ fill: 'blue', shape: 'dot', text: `respond ok √ó${cnt}` });

return msg;

// encrypt the picture and publish (e2e data run with KMS meta + t_pub + duration)

const fs = global.get('fs');
const crypto = global.get('crypto');

// ---------- 0) Resolve topic/key ----------
let topicName = msg.topicName || flow.get('topicName') || flow.get('topic_name') || '';
let keyBlobB64 = msg.keyBlobB64;

if (!keyBlobB64) {
  const topicKeys = flow.get('topicKeys') || {};
  if (!topicName) topicName = Object.keys(topicKeys)[0] || '';
  keyBlobB64 = topicName ? topicKeys[topicName] : undefined;
}
if (!topicName || !keyBlobB64) {
  node.error('‚ùå Missing topicName or key for this topic in flow/message context.');
  return null;
}

// ---------- 1) Normalize to a 32-byte key ----------
function tryBase64(s) { try { return Buffer.from(s, 'base64'); } catch (e) { return Buffer.alloc(0); } }
function tryBase64Url(s) {
  let t = s.replace(/-/g, '+').replace(/_/g, '/');
  const pad = t.length % 4;
  if (pad === 2) t += '==';
  else if (pad === 3) t += '=';
  else if (pad !== 0) t += '==';
  return tryBase64(t);
}
function tryHex(s) {
  try {
    return (/^[0-9A-Fa-f]+$/.test(s) && (s.length % 2 === 0)) ? Buffer.from(s, 'hex') : Buffer.alloc(0);
  } catch (e) {
    return Buffer.alloc(0);
  }
}
const raw = String(keyBlobB64).replace(/^"(.*)"$/, '$1').replace(/\s+/g, '');
let key = tryBase64(raw);
if (key.length !== 32) key = tryBase64Url(raw);
if (key.length !== 32) key = tryHex(raw);
if (key.length === 33 && (key[32] === 0x0A || key[32] === 0x00)) key = key.slice(0, 32);
if (key.length !== 32) {
  node.error(`‚ùå Invalid key length after normalization: expected 32 bytes, got ${key.length} (inputLen=${raw.length})`);
  return null;
}

// ---------- 2) Read file ----------
const imgPath = '/etc/mosquitto/certs/test.png';
const filename = 'test.png';
if (!fs.existsSync(imgPath)) {
  node.error(`‚ùå File not found: ${imgPath}`);
  return null;
}
const plaintext = fs.readFileSync(imgPath);

// ---------- 3) Encrypt (AES-256-CBC + PKCS#7) ----------
const iv = crypto.randomBytes(16);

// 3a) Encrypt image
const cipherImg = crypto.createCipheriv('aes-256-cbc', key, iv);
const ctImage = Buffer.concat([cipherImg.update(plaintext), cipherImg.final()]);

// 3b) Encrypt additional text with SAME key and SAME IV
const ts = Math.floor(Date.now() / 1000);
const humanTime = new Date(ts * 1000).toLocaleString();
const note = `This picture is send from Bob ${ts}`;

const cipherNote = crypto.createCipheriv('aes-256-cbc', key, iv);
const ctNote = Buffer.concat([cipherNote.update(Buffer.from(note, 'utf8')), cipherNote.final()]);

// ---------- 3c) Sign plaintext + note with Bob's private key ----------
const deviceID      = global.get('deviceID', 'file');
const privateKeyPEM = global.get('private_key', 'file');

if (!deviceID || !privateKeyPEM) {
  node.error('‚ùå Missing deviceID or private_key in global(file) context for signing.');
  return null;
}

let signatureB64;
try {
  const signer = crypto.createSign('sha256');
  // Sign both the image bytes and the note text
  signer.update(plaintext);
  signer.update(Buffer.from(note, 'utf8'));
  signer.end();
  signatureB64 = signer.sign(privateKeyPEM, 'base64');
} catch (e) {
  node.error('‚ùå Signing failed: ' + e.message);
  return null;
}

// ---------- 4) Pull KMS run/test meta (from Sunny's respond) ----------
const kmsMeta       = flow.get('kms_meta_from_resp') || {};
const runId        = String(kmsMeta.run_id || '');
const runTag       = String(kmsMeta.run_tag || '');
const rateMps      = Number(kmsMeta.rate_mps || 0);
const msgCountMeta = Number(kmsMeta.msg_count || 0);
const durationSec  = Number(kmsMeta.duration || 0) || 120;   // duration for the run
const tPubFromReg  = Number(kmsMeta.t_pub || 0);             // original t_pub from registration

const runKey  = runId || runTag || 'default';
const seqKey   = `e2e_seq_${runKey}`;
const totalKey = `e2e_total_${runKey}`;

let seq   = Number(flow.get(seqKey)   || 0);
let total = Number(flow.get(totalKey) || 0);

if (!Number.isFinite(total) || total <= 0) {
  if (Number.isFinite(msgCountMeta) && msgCountMeta > 0) {
    total = msgCountMeta;
  } else if (Number.isFinite(rateMps) && rateMps > 0) {
    total = Math.floor(rateMps * durationSec);
  } else {
    total = 1; // fallback if nothing known
  }
  flow.set(totalKey, total);
}

seq += 1;
flow.set(seqKey, seq);

let kms_control;
if (seq >= total) {
  kms_control = 'done';
}

// ---------- 5) Get SSID ----------
const wifi_ssid = String(flow.get('wifi_ssid') || '').trim();
if (!wifi_ssid) {
  node.error('‚ùå Missing wifi_ssid in flow');
  return null;
}

// ---------- 6) Build MQTT payload (plaintext meta + ciphertext data) ----------
// Use original registration t_pub as t_pub for end-to-end latency
const t_pub = Number.isFinite(tPubFromReg) && tPubFromReg > 0 ? tPubFromReg : Date.now();

const payload = {
  alg: 'AES-256-CBC',
  iv_b64: iv.toString('base64'),
  ct_b64: ctImage.toString('base64'),
  filename,
  mime: 'image/png',
  msg_ct_b64: ctNote.toString('base64'),
  msg_ts: ts,
  wifi_ssid,

  // NEW: sender identity + signature over plaintext+note
  sender_device_id: deviceID,
  signature_b64: signatureB64,

  // top-level KMS meta (plaintext) so Bob can start scripts
  run_id: runId,
  run_tag: runTag,
  mode: kmsMeta.mode,
  path_class: kmsMeta.path_class,
  mqtt_version: kmsMeta.mqtt_version,
  qos: kmsMeta.qos,
  rate_mps: kmsMeta.rate_mps,
  msg_count: msgCountMeta,
  duration: durationSec,
  t_pub,
  seq,
  total
};

if (kms_control) {
  payload.kms_control = kms_control;   // only present on last message
}

// also keep nested kms_meta for logging/consistency
payload.kms_meta = {
  run_id: runId,
  run_tag: runTag,
  mode: kmsMeta.mode,
  path_class: kmsMeta.path_class,
  mqtt_version: kmsMeta.mqtt_version,
  qos: kmsMeta.qos,
  rate_mps: kmsMeta.rate_mps,
  msg_count: msgCountMeta,
  duration: durationSec,
  t_pub,
  seq,
  total,
  kms_control: kms_control || ''
};

msg.topic = topicName;
msg.payload = payload;

node.warn(`üì§ Encryption + signing complete for topic "${topicName}" ‚Üí publish seq=${seq}/${total} (run_id=${runId})`);
return msg;

// Function ‚Äî Build encrypted Topic-Key-Request (minimal payload to Sunny)
// Parses sender public key from Exec output and saves to flow,
// and sends exactly ONE Topic-Key-Request per Exec invocation
// (per msg._msgid), so multiple stdout chunks do not cause duplicates.

const fs     = global.get('fs');
const crypto = global.get('crypto');

// ========== (0) PER-EXEC DE-DUP USING msg._msgid ==========

const msgId  = msg._msgid || "";
const doneKey = msgId ? `tkreq_done_${msgId}` : null;

// If this Exec invocation has already produced a Topic-Key-Request,
// ignore any further stdout chunks.
if (doneKey && flow.get(doneKey)) {
    node.warn(`‚ÑπÔ∏è Topic-Key-Request already sent for this Exec invocation (msgid=${msgId}), skipping duplicate chunk.`);
    return null;
}

// ========== (A) PARSE SENDER PUBLIC KEY IF PRESENT IN msg.payload ==========

let hasPem = false;

try {
    const raw = msg.payload;

    if (raw && typeof raw === "object") {
        // Case 1: Exec (or another node) returned an object with public_key field
        if (raw.public_key) {
            const cleaned = String(raw.public_key).trim();

            if (/-----BEGIN PUBLIC KEY-----[\s\S]*?-----END PUBLIC KEY-----/.test(cleaned)) {
                flow.set("sender_public_key_pem", cleaned);
                hasPem = true;
            } else {
                node.warn("‚ö†Ô∏è public_key field found but not a valid PEM block. Skipping this message.");
                return null;
            }
        } else {
            // No public_key in this object ‚Üí nothing to do, ignore this chunk
            return null;
        }

    } else if (typeof raw === "string") {
        // Case 2: Exec returned raw text; try to extract the PEM block
        const match = raw.match(/-----BEGIN PUBLIC KEY-----[\s\S]*?-----END PUBLIC KEY-----/);

        if (match) {
            const pem = match[0].trim();
            flow.set("sender_public_key_pem", pem);
            hasPem = true;
        } else {
            // This string chunk does NOT contain PEM (could be "", "wa", etc.) ‚Üí ignore
            return null;
        }
    } else {
        // Unsupported payload type, ignore this exec chunk
        return null;
    }
} catch (e) {
    node.warn("‚ö†Ô∏è Failed to parse sender public key: " + e.message);
    return null;
}

// Safety: if no PEM was found in this message, do not continue
if (!hasPem) {
    return null;
}

// ========== (B) NORMAL TOPIC-KEY-REQUEST BUILD ==========

// 1) Context
const topic_name = flow.get('topic_name');
const deviceID   = global.get('deviceID', 'file');
const signature  = flow.get('signature');
const wifi_ssid  = flow.get('wifi_ssid');

if (!topic_name) { node.error('‚ùå Missing topic_name in flow'); return null; }
if (!deviceID)   { node.error('‚ùå Missing deviceID in global(file) context'); return null; }
if (!signature)  { node.error('‚ùå Missing signature in flow'); return null; }

// 2) Load topic-key-request shared secret
const secretPath = '/etc/mosquitto/certs/alice-topic-key-request-secret-key.bin';
if (!fs.existsSync(secretPath)) {
  node.error(`‚ùå Secret key not found: ${secretPath}`);
  return null;
}
const secretBin = fs.readFileSync(secretPath);
const aesKey    = crypto.createHash('sha256').update(secretBin).digest();

// 3) Encrypt payload { deviceID, topic_name }
const iv        = crypto.randomBytes(16);
const plaintext = JSON.stringify({ deviceID, topic_name });
const cipher    = crypto.createCipheriv('aes-256-cbc', aesKey, iv);
let encrypted   = cipher.update(plaintext, 'utf8', 'hex');
encrypted      += cipher.final('hex');

// Store timestamp (not sent)
const now = Date.now();
flow.set('t_keyreq_publish', now);

// 4) Build outbound payload
msg.payload = {
  encryptedData: encrypted,
  iv:           iv.toString('hex'),
  signature:    signature,
  device_id:    deviceID,
  wifi_ssid:    wifi_ssid
};

// Mark this Exec invocation as completed so further chunks are ignored
if (doneKey) {
    flow.set(doneKey, true);
}

node.warn(`Encrypt / Sign Topic-Key-Request ready for topic "${topic_name}" from Alice`);
return msg;

const rows = Array.isArray(msg.payload) ? msg.payload : [];
let list = flow.get('registeredTopics') || [];
for (const r of rows) { const t = String(r.topic || '').trim(); if (t && !list.includes(t)) list.push(t); }
flow.set('registeredTopics', list);
return null;

let list = flow.get('registeredTopics') || [];
list = list.map(t => String(t)).filter(Boolean);
let current = flow.get('topic_name') || '';
if (!current && list.length) current = list[0];
return [{ options: list, payload: current }];

const topic = String(msg.payload || '').trim();
if (!topic) { return null; }
let list = flow.get('registeredTopics') || [];
if (!list.includes(topic)) list.push(topic);
flow.set('registeredTopics', list);
flow.set('topic_name', topic);
node.send([ { action: 'rebuild' }, { payload: topic } ]);
return null;

// Function ‚Äî Receive encrypted data, cache it, capture KMS meta,
// prepare key-req (ECDH secret + sender public-key query), and start monitors

const fs = global.get("fs");
const crypto = global.get("crypto");

// === 1) Extract inbound payload ===
const topic_name = String(msg.topic || "");
const p = msg.payload || {};

const iv_b64       = String(p.iv_b64 || "").trim();
const ct_b64       = String(p.ct_b64 || "").trim();
const filename     = String(p.filename || "").trim();
const ct_nt_b64    = String(p.msg_ct_b64 || "").trim();
const senderDeviceID = String(p.sender_device_id || "").trim();

// --- 1a) Quick MITM filter: ignore original packets in MITM run ---
// We look at mode + run_tag directly from payload / kms_meta.
// If run_tag == "MITM" and mode != "cookie_sig_shift_3", this is Alice's
// original message; we do NOT want it to overwrite flow state.
const kmQuick = p.kms_meta || {};
const rawMode   = (p.mode !== undefined) ? p.mode : kmQuick.mode;
const rawRunTag = String(p.run_tag || kmQuick.run_tag || "");

if (rawRunTag === "MITM" && rawMode !== "cookie_sig_shift_3") {
  // Ignore this message completely for key-request/decrypt pipeline
  // (only Cookie's tampered messages should be processed in MITM test)
  return [null, null, null];
}

// Cache cipher + file info for later decryption
flow.set("topic_name", topic_name);
flow.set("iv_b64", iv_b64);
flow.set("ct_b64", ct_b64);
flow.set("filename", filename);
flow.set("ct_nt_b64", ct_nt_b64);

// cache sender signature for final verification
const senderSigB64 =
  String(p.sender_signature_b64 || p.signature_b64 || "").trim();

if (senderSigB64) {
  flow.set("sender_signature_b64", senderSigB64);
   //node.warn("‚úî Sender signature saved to flow (sender_signature_b64)");
} else {
  node.warn("‚ö† No sender signature found in incoming payload.");
}

// --- 2) Capture KMS / E2E meta (from top-level or nested kms_meta) ---
const km = p.kms_meta || {};
const meta = {
  run_id:       String(p.run_id || km.run_id || ""),
  run_tag:      String(p.run_tag || km.run_tag || ""),
  mode:         (p.mode !== undefined) ? p.mode : km.mode,
  path_class:   (p.path_class !== undefined)   ? p.path_class   : km.path_class,
  mqtt_version: (p.mqtt_version !== undefined) ? p.mqtt_version : km.mqtt_version,
  qos:          (p.qos !== undefined)          ? p.qos          : km.qos,
  rate_mps:     (p.rate_mps !== undefined)     ? p.rate_mps     : km.rate_mps,
  msg_count:    Number(p.msg_count ?? km.msg_count ?? 0),
  duration:     Number(p.duration ?? km.duration ?? 0),
  t_pub:        Number(p.t_pub ?? km.t_pub ?? 0),

  // track seq / total explicitly
  seq:   Number(p.seq ?? km.seq ?? 0),
  total: Number(p.total ?? km.total ?? 0),

  kms_control: p.kms_control || km.kms_control || ""
};
flow.set("kms_meta_alice", meta);

// --- mark run "done" if control OR high coverage by seq/total ---
const runKey = String(meta.run_id || meta.run_tag || "default");

// expected by seq (from Alice‚Äôs view)
const expectedBySeq =
  Number.isFinite(meta.total) && meta.total > 0 ? meta.total : 0;

const coverage = expectedBySeq > 0 && meta.seq > 0
  ? meta.seq / expectedBySeq
  : 0;

// Threshold: if we saw ‚â•95% of expected seqs, we still summarise
const COVERAGE_THRESHOLD = 0.95;

if (
  meta.kms_control === "done" ||
  (coverage >= COVERAGE_THRESHOLD && expectedBySeq > 0)
) {
  flow.set(`secure_e2e_done_${runKey}`, true);
}

// === 3) Build signature `${deviceID}|${topic_name}` for topic-key request ===
const deviceID = global.get("deviceID", "file");
const privateKeyPEM = global.get("private_key", "file");
if (!deviceID || !privateKeyPEM) {
  node.error("‚ùå Missing deviceID or private_key in global(file) context.");
  return [null, null, null];
}

const signer = crypto.createSign("sha256");
signer.update(`${deviceID}|${topic_name}`);
signer.end();
const signature = signer.sign(privateKeyPEM, "base64");
flow.set("signature", signature);

// === 4) Prepare commands for Exec node (ECDH derive + SELECT public_key) ===
const execMsgs = [];

// 4a) ECDH derive secret for topic-key-request
const certDir  = "/etc/mosquitto/certs";
const bobPriv  = `${certDir}/alice-private-key.pem`;
const sunnyPub = `${certDir}/sunny-public-key.pem`;
const outSecret = `${certDir}/alice-topic-key-request-secret-key.bin`;

if (fs) {
  if (!fs.existsSync(bobPriv))  node.warn(`‚ö†Ô∏è Missing ${bobPriv}`);
  if (!fs.existsSync(sunnyPub)) node.warn(`‚ö†Ô∏è Missing ${sunnyPub}`);
}

const opensslCmd =
  `openssl pkeyutl -derive -inkey ${bobPriv} -peerkey ${sunnyPub} -out ${outSecret}`;
execMsgs.push({ payload: opensslCmd });

// 4b) SQLCipher query: get sender's public key from Keys table (with WAL)
if (!senderDeviceID) {
  node.warn("‚ö†Ô∏è sender_device_id not present in payload; skipping public_key SELECT.");
} else {
  const dbPath    = "/etc/mosquitto/certs/update_database.db";
  const pragmaKey = env.get("DB_PASSWORD");
  const safeDevID = senderDeviceID.replace(/'/g, "''");

  const sqlQuery = `
PRAGMA key='${pragmaKey}';
PRAGMA journal_mode=WAL;
SELECT public_key FROM Keys WHERE device_id = '${safeDevID}';
`.trim();

  // Use your usual here-string pattern
  const sqlCmd = `echo "${sqlQuery.trim()}" | sqlcipher ${dbPath}`;
  execMsgs.push({ payload: sqlCmd });
}

// === 5) Start monitors (mosquitto + node-red) once per run, with duration ===
let startMon1 = null;
let startMon2 = null;

const monKey = `alice_mon_${runKey}`;

if (!flow.get(monKey)) {
  const scriptPath   = "/data/data/com.termux/files/home/.node-red/perf_results/monitor_sys_stats.sh";
  const qosArg       = String(meta.qos ?? "");
  const mqttArg      = String(meta.mqtt_version ?? "");
  const runTag       = String(meta.run_tag || "run");
  const dur          = Number.isFinite(meta.duration) && meta.duration > 0 ? meta.duration : 120;
  const durationArg  = String(dur);

  startMon1 = {
    payload: `${scriptPath} ${runTag} mosquitto ${qosArg} ${mqttArg} ${durationArg}`
  };
  startMon2 = {
    payload: `${scriptPath} ${runTag} node-red ${qosArg} ${mqttArg} ${durationArg}`
  };

  flow.set(monKey, true);
  node.status({ fill: "blue", shape: "dot",
                text: `monitors started (${runTag}, ${durationArg}s)` });
}

// Output1 -> Exec node (two commands), Output2 -> start mosquitto monitor,
// Output3 -> start node-red monitor
return [execMsgs, startMon1, startMon2];

// Function ‚Äî Decrypt topic-key response (ECDH secret), verify Sunny's response
// signature, decrypt image+note for Dashboard, VERIFY sender's data signature,
// and log secure E2E latency.
//
// Outputs:
//  1) to File node (decrypted image file)          (only when status === "OK")
//  2) to Dashboard (ui_template)                   (only when status === "OK")
//  3) to bob_perf_secure.csv (per-message rows)
//  4) to summary_latency_secure.csv (per-run summary with drop %)

const fs = global.get("fs");
const crypto = global.get("crypto");

// ---------- A) Decrypt & VERIFY the topic-key RESPONSE ----------
try {
  const encHex = String(msg.payload?.encryptedData || "").trim();
  const ivHex  = String(msg.payload?.iv || "").trim();
  const sigB64 = String(msg.payload?.signature || "").trim();

  if (!encHex || !ivHex || !sigB64) {
    node.error("‚ùå Missing encryptedData, iv, or signature in topic/key_resp payload.");
    return [null, null, null, null];
  }

  const secretPath = "/etc/mosquitto/certs/alice-topic-key-request-secret-key.bin";
  if (!fs.existsSync(secretPath)) {
    node.error(`‚ùå Secret key file not found: ${secretPath}`);
    return [null, null, null, null];
  }
  const secretBin = fs.readFileSync(secretPath);
  const aesKey = crypto.createHash("sha256").update(secretBin).digest();

  const ivBuf = Buffer.from(ivHex, "hex");
  const ctBuf = Buffer.from(encHex, "hex");
  if (ivBuf.length !== 16) {
    node.error(`‚ùå Invalid IV length ${ivBuf.length} (expected 16)`);
    return [null, null, null, null];
  }
  const decipherResp = crypto.createDecipheriv("aes-256-cbc", aesKey, ivBuf);
  const plaintextResp = Buffer.concat([decipherResp.update(ctBuf), decipherResp.final()]).toString("utf8");

  const respObj = JSON.parse(plaintextResp);
  const topicNameDecrypted = String(respObj.topic_name || "");
  const keyBlobB64Resp     = String(respObj.key_blob_b64 || "");
  const statusResp         = String(respObj.status || "UNKNOWN");

  if (!topicNameDecrypted || !keyBlobB64Resp) {
    node.error("‚ùå Decrypted response missing topic_name or key_blob_b64.");
    return [null, null, null, null];
  }

  const sunnyPubPath = "/etc/mosquitto/certs/sunny-public-key.pem";
  if (!fs.existsSync(sunnyPubPath)) {
    node.error(`‚ùå Sunny public key not found: ${sunnyPubPath}`);
    return [null, null, null, null];
  }
  const sunnyPubPEM = fs.readFileSync(sunnyPubPath, "utf8").trim();

  const signedString = `${statusResp}|${topicNameDecrypted}|${keyBlobB64Resp}`;
  let sigOK = false;
  try {
    const verifier = crypto.createVerify("sha256");
    verifier.update(signedString);
    verifier.end();
    sigOK = verifier.verify(sunnyPubPEM, sigB64, "base64");
  } catch (e) {
    node.error("‚ùå Signature verification error (Sunny response): " + e.message);
    return [null, null, null, null];
  }
  if (!sigOK) {
    node.error("‚ùå Topic-key response signature INVALID (Sunny). Dropping message.");
    return [null, null, null, null];
  }

  const cleanedKey = keyBlobB64Resp.replace(/^"(.*)"$/, "$1").replace(/\s+/g, "");
  const topicKeys  = Object.assign({}, flow.get("topicKeys") || {});
  topicKeys[topicNameDecrypted] = cleanedKey;
  flow.set("topicKeys", topicKeys);

  msg._topic_name_from_keyresp = topicNameDecrypted;
  msg._key_b64_from_keyresp    = cleanedKey;

} catch (e) {
  node.error("‚ùå Failed to decrypt/verify topic-key response: " + e.message);
  return [null, null, null, null];
}

// ---------- B) Decrypt image + note ----------
const topicName  = msg._topic_name_from_keyresp || String(msg.topic || "");
const keyBlobB64 = msg._key_b64_from_keyresp   || "";

const iv_b64    = String(flow.get("iv_b64")    || "");
const ct_b64    = String(flow.get("ct_b64")    || "");
const ct_nt_b64 = String(flow.get("ct_nt_b64") || "");
const filename  = String(flow.get("filename")  || "file.bin");

if (!topicName || !keyBlobB64 || !iv_b64 || !ct_b64) {
  node.error("‚ùå Missing topic_name/key or iv/ct from flow to decrypt the data message.");
  return [null, null, null, null];
}

// Meta is needed both for status + logging (mode, run_tag, etc.)
const meta   = flow.get("kms_meta_bob") || {};
const runId  = String(meta.run_id || "");
const runTag = String(meta.run_tag || "");

// Status of this message (for CSV)
let status = "OK";
// Whether to send to file/dashboard
let deliverToUser = true;

function normalizeKey(b64) {
  const clean = b64.replace(/^"(.*)"$/, "$1").replace(/\s+/g, "");
  let k = Buffer.from(clean, "base64");
  if (k.length === 33 && (k[32] === 0x0A || k[32] === 0x00)) k = k.slice(0, 32);
  return k;
}
const key = normalizeKey(keyBlobB64);
if (key.length !== 32) {
  node.error(`‚ùå Invalid key length: ${key.length} (expected 32)`);
  status = "decrypt_fail";
  deliverToUser = false;
}

// Even if AES fails, we still want plaintext defined for logging
let plaintext = Buffer.alloc(0);

if (status === "OK") {
  const iv = Buffer.from(iv_b64, "base64");
  const ct = Buffer.from(ct_b64, "base64");
  if (iv.length !== 16) {
    node.error(`‚ùå Invalid IV length ${iv.length} (expected 16)`);
    status = "decrypt_fail";
    deliverToUser = false;
  } else {
    try {
      const decipher = crypto.createDecipheriv("aes-256-cbc", key, iv);
      plaintext = Buffer.concat([decipher.update(ct), decipher.final()]);
    } catch (e) {
      node.error("‚ùå Decryption failed (image): " + e.message);
      status = "decrypt_fail";
      deliverToUser = false;
    }
  }
}

let noteText  = "";
let noteTs    = null;
let noteHuman = "";
let noteMsg   = "";

if (ct_nt_b64 && status === "OK") {
  try {
    const ctNote  = Buffer.from(ct_nt_b64, "base64");
    const decNote = crypto.createDecipheriv("aes-256-cbc", key, Buffer.from(iv_b64, "base64"));
    noteText = Buffer.concat([decNote.update(ctNote), decNote.final()]).toString("utf8");

    const m = noteText.match(/(\d{10,})\s*$/);
    if (m) {
      noteTs    = Number(m[1]);
      noteMsg   = noteText.slice(0, m.index).trim();
      noteHuman = new Date(noteTs * 1000).toLocaleString();
    } else {
      noteMsg = noteText.trim();
    }
  } catch (e) {
    node.warn("‚ö†Ô∏è Decryption of note failed: " + e.message);
  }
} else if (!ct_nt_b64) {
  node.warn("‚ÑπÔ∏è No encrypted note text found in flow context.");
}

// ---------- C) VERIFY sender's data signature ----------
//
// Sender signed: signer.update(imagePlaintext); signer.update(Buffer.from(note, "utf8"));
if (status === "OK") {
  try {
    const senderSigB64 = String(flow.get("sender_signature_b64") || "").trim();
    const senderPubPEM = String(flow.get("sender_public_key_pem") || "").trim();

    if (!senderSigB64 || !senderPubPEM) {
      node.warn("‚ö†Ô∏è Missing sender signature or public key in flow; skipping sender data signature verification.");
    } else if (!noteText) {
      node.warn("‚ö†Ô∏è Decrypted note text empty; cannot safely verify sender signature. Skipping verification.");
    } else {
      const verifier = crypto.createVerify("sha256");
      verifier.update(plaintext);                     // image bytes
      verifier.update(Buffer.from(noteText, "utf8")); // note text
      verifier.end();

      const sigOK = verifier.verify(senderPubPEM, senderSigB64, "base64");
      if (!sigOK) {
        // Distinguish MITM vs other signature failures
        const mode = String(meta.mode || "");
        status = (mode === "cookie_sig_shift_3") ? "attack_sig_fail" : "sig_fail";
        deliverToUser = false;
        node.error("‚ùå Sender data signature INVALID (image + note). Dropping decrypted content.");
        node.warn(`‚ö†Ô∏è Debug: status=${status}, signature_len=${senderSigB64.length}, note_len=${noteText.length}, img_bytes=${plaintext.length}`);
      } else {
        node.warn("‚úÖ Sender data signature verified successfully.");
      }
    }
  } catch (e) {
    node.error("‚ùå Error during sender data signature verification: " + e.message);
    status = "sig_fail";
    deliverToUser = false;
  }
}

// ---------- D) Outputs for file + dashboard ----------
const safeName = filename.replace(/[^A-Za-z0-9._-]/g, "_");

const toFile = {
  filename: `/etc/mosquitto/certs/${safeName}`,
  payload: plaintext
};

const toDashboard = {
  topic: topicName,
  payload: {
    image_data_url: "data:image/png;base64," + plaintext.toString("base64"),
    note_text_raw: noteText,
    note_message: noteMsg,
    note_ts: noteTs,
    note_human: noteHuman,
    filename: safeName
  }
};

if (status === "OK") {
  node.warn(`‚úÖ Full decrypt complete for topic "${topicName}" ‚Üí content decrypted & signature checked ‚Üí ready for file write & dashboard display.`);
} else {
  node.warn(`‚ÑπÔ∏è No file/dashboard output for topic "${topicName}" because status=${status}.`);
}

// ---------- E) Secure E2E performance logging ----------
let perfMsgs = [];
let sumMsgs  = [];

try {
  function fmt(ms) {
    if (!Number.isFinite(ms)) return "";
    const d  = new Date(ms);
    const p  = n => String(n).padStart(2, "0");
    const ms3= String(d.getMilliseconds()).padStart(3, "0");
    return `${d.getFullYear()}-${p(d.getMonth()+1)}-${p(d.getDate())} `
         + `${p(d.getHours())}:${p(d.getMinutes())}:${p(d.getSeconds())}.${ms3}`;
  }
  function csv(arr) {
    return arr.map(v => (v === undefined ? "" : v)).join(",");
  }

  const runKey = runId || runTag || "default";

  const perfPath = "/etc/mosquitto/perf_results/bob_perf_secure.csv";
  const sumPath  = "/etc/mosquitto/perf_results/summary_latency_secure.csv";

  const now   = Date.now();
  const t_pub = Number(meta.t_pub || 0);

  const DURATION_SEC = Number(meta.duration || 120) || 120;
  const rateMps      = Number(meta.rate_mps || 0);
  const msgCountMeta = Number(meta.msg_count || 0);

  let expectedBase;
  if (Number.isFinite(msgCountMeta) && msgCountMeta > 0) {
    expectedBase = Math.round(msgCountMeta);
  } else if (Number.isFinite(rateMps) && rateMps > 0) {
    expectedBase = Math.round(rateMps * DURATION_SEC);
  } else {
    expectedBase = 0;
  }

  const stateKey = `secure_e2e_${runKey}`;
  let state = flow.get(stateKey) || {
    latencies: [],
    count: 0,
    expected: expectedBase,
    firstRecv: 0,
    lastRecv: 0,
    summaryWritten: false
  };
  if (!state.expected && expectedBase > 0) state.expected = expectedBase;

  if (!state.firstRecv) state.firstRecv = now;
  state.lastRecv = now;

  let latMs = NaN;
  // Only treat as latency sample when we actually succeeded (status === "OK")
  if (status === "OK" && Number.isFinite(t_pub) && t_pub > 0) {
    latMs = now - t_pub;
    state.latencies.push(latMs);
  }
  state.count += 1;
  flow.set(stateKey, state);

  // payload_bytes: decrypted image bytes + note bytes (even if status != OK we can log size)
  let payloadBytes = 0;
  if (plaintext && Buffer.isBuffer(plaintext)) {
    payloadBytes += plaintext.length;
  }
  if (noteText) {
    payloadBytes += Buffer.byteLength(noteText, "utf8");
  }

  const perfHeader = csv([
    "ts_human","mode","path_class","run_tag","mqtt_version","qos","rate_mps",
    "run_id","topic","payload_bytes","t_pub_human","t_recv_human",
    "latency_ms","latency_s","status"
  ]);

  const perfRow = csv([
    fmt(now),
    meta.mode,
    meta.path_class,
    meta.run_tag,
    meta.mqtt_version,
    meta.qos,
    meta.rate_mps,
    runId || runTag || "",
    topicName,
    payloadBytes,
    fmt(t_pub),
    fmt(now),
    Number.isFinite(latMs) ? latMs : "",
    Number.isFinite(latMs) ? (latMs/1000).toFixed(3) : "",
    status
  ]);

  const perfHdrWritten = !!flow.get("bob_perf_secure_hdr");
  const perfFileMissing = (fs && fs.exists
    ? !fs.existsSync(perfPath)
    : !perfHdrWritten);
  if (!perfHdrWritten || perfFileMissing) {
    perfMsgs.push({ payload: perfHeader, filename: perfPath });
    flow.set("bob_perf_secure_hdr", true);
  }
  perfMsgs.push({ payload: perfRow, filename: perfPath });

  // ---- summary row logic (expected vs observed + drop %) ----
  const doneFlag      = !!flow.get(`secure_e2e_done_${runKey}`);
  const graceMs       = 5000;
  const timeExpired   = state.firstRecv && (now - state.firstRecv) > (DURATION_SEC * 1000 + graceMs);
  const enoughSamples = state.expected > 0 && state.latencies.length >= state.expected;

  if (!state.summaryWritten && (doneFlag || timeExpired || enoughSamples)) {
    const arrRaw = state.latencies.slice();
    if (arrRaw.length > 0) {
      const observedCount = arrRaw.length;

      let expected = state.expected || expectedBase || observedCount;
      if (expected < observedCount) expected = observedCount;

      let dropCount = expected - observedCount;
      if (dropCount < 0) dropCount = 0;

      const dropPct = expected > 0 ? (dropCount / expected) * 100 : 0;

      const arr = arrRaw.sort((a, b) => a - b);
      const pick = p => arr[Math.min(arr.length - 1, Math.floor(p * (arr.length - 1)))];
      const median = pick(0.5);
      const p95    = pick(0.95);
      const p99    = pick(0.99);

      const sumHeader = csv([
        "ts_human","mode","path_class","run_tag","mqtt_version","qos","rate_mps",
        "run_id","count","expected_count","estimated_drop_count","estimated_drop_pct",
        "median_ms","p95_ms","p99_ms","median_s","p95_s","p99_s"
      ]);

      const sumRow = csv([
        fmt(Date.now()),
        meta.mode,
        meta.path_class,
        meta.run_tag,
        meta.mqtt_version,
        meta.qos,
        meta.rate_mps,
        runId || runTag || "default",
        observedCount,
        expected,
        dropCount,
        dropPct.toFixed(1),
        median,
        p95,
        p99,
        (median/1000).toFixed(3),
        (p95/1000).toFixed(3),
        (p99/1000).toFixed(3)
      ]);

      const sumHdrWritten = !!flow.get("bob_sum_secure_hdr");
      const sumFileMissing = (fs && fs.exists
        ? !fs.existsSync(sumPath)
        : !sumHdrWritten);
      if (!sumHdrWritten || sumFileMissing) {
        sumMsgs.push({ payload: sumHeader, filename: sumPath });
        flow.set("bob_sum_secure_hdr", true);
      }
      sumMsgs.push({ payload: sumRow, filename: sumPath });
    }

    state.summaryWritten = true;
    flow.set(stateKey, state);
    flow.set(`secure_e2e_done_${runKey}`, false);
  }

} catch (e) {
  node.warn("‚ö†Ô∏è Secure E2E logging failed: " + e.message);
}

// Only send file/dashboard if status === "OK"
const outFile = (status === "OK") ? toFile : null;
const outDash = (status === "OK") ? toDashboard : null;

return [outFile, outDash, perfMsgs, sumMsgs];
